<div class="shop-page hiddenTable tableNumber2">
    <div id="add-item" class="add-something toggleOpenPopup floating-plus-btn d-flex bg-primary cursor-pointer" data-target="mainShopPopup">
        <i class="fal fa-plus fa-lg margin-a"></i>
    </div>
    <div class="shop-top-bar d-flex justify-content-start">
        <input type="search" id="search-shop2" onsearch="OnSearch(this)" class="search text-start " placeholder="חיפוש" aria-controls="shopItemsTable" onsearch="OnSearch(this)"/>
    </div>
    <table class="table table-hover dt-responsive text-start display wrap dataTable no-footer dtr-inline"  id="shopItemsTable" cellspacing="0" width="100%" role="grid" aria-describedby="shopItemsTable_info" style="width: 100%;">
        <thead>
            <tr role="row">
                <th class="text-start " tabindex="0" aria-controls="shopItemsTable" rowspan="1" colspan="1" style="width: 30%; border-top: none;" aria-label="שם פריט: activate to sort column ascending">שם פריט</th>
                <th class="text-start " tabindex="0" aria-controls="shopItemsTable" rowspan="1" colspan="1" style="width: 15%; border-top: none;" aria-label="מלאי: activate to sort column ascending">מלאי</th>
                <th class="text-start " tabindex="0" aria-controls="shopItemsTable" rowspan="1" colspan="1" style="width: 15%; border-top: none;" aria-label="אפליקציה: activate to sort column ascending">אפליקציה</th>
                <th class="text-start " tabindex="0" aria-controls="shopItemsTable" rowspan="1" colspan="1" style="width: 18%; border-top: none;" aria-label="סניף: activate to sort column ascending">סניף</th>
                <th class="text-start " tabindex="0" aria-controls="shopItemsTable" rowspan="1" colspan="1" style="width: 9%; border-top: none;" aria-label="מחיר: activate to sort column ascending">מחיר</th>
                <th class="text-start" tabindex="0" aria-controls="shopItemsTable" rowspan="1" colspan="1" style="width: 10%; border-top: none;" aria-label="שם פריט: activate to sort column ascending"></th>
            </tr>
        </thead>
    </table>
</div>